    # sum of a[0] to a[n-1]
    sum_a = sum(a)

    # get the first value that's not 0
    for i in range(n):
        if a[i]!= 0:
            break

    # if sum is the same then all elements are the same
    if sum_a == a[i]:
        return str(0)

    # if the sum is greater than the sum of a[i], we can
