    def calculate(a: int, b: int) -> int:
        """
        return the minimum number of operations to obtain $b$ from $a$
        """
        if a == b:
            return 0
        if a == 0:
            return b
        if b == 0:
            return a

        # Since we know that $b$ is divisible by $a$, we can divide $a$ by $b$ and do this repeatedly
        # The last result is a valid triple
        if a < b:
            return calculate(a, a % b) + b
        else:
            return calculate(a, a % b) + a

    res = []
