    # sort s
    s = sorted(s)
    cost = 0
    left = 0
    for i in range(1, n):
        if s[i]!= s[i - 1]:
            left = i
            break
    s = s[:left]

    # 2-s algorithm
    start = 0
    end = left
    while start < left and end < n:
        cost += left - start
        end += 1
        while end < n and s[start] == s[end]:
            end += 1
        start += 1
    cost += end - left
    if end < n:
        cost += n - end
    return str(cost)

