    for _ in range(m):
        u, v = map(int, stdin.split(' '))
        graph[u].add(v)
        graph[v].add(u)

    # the k-out of n can be used for the first vertex (with label 1)
    for k in range(2, n + 1):
        to_visit = set()
        for vertex in range(1, n + 1):
            if k == len(graph[vertex]):
                to_visit.add(vertex)
        if len(to_visit) == 0:
            break

        while len(to_visit) > 0:
            vertex = to_visit.pop()
            d = len(graph[vertex])
            if d == k:
                for v in graph[vertex]:
                    graph[v].remove(vertex)
                continue
            elif d < k:
                for v in graph[vertex]:
                    graph[v].remove(vertex)
                graph[vertex].add(k)
                to_visit.add(k)

    for vertex in range(1, n + 1):
        if len(graph[vertex]) == d:
            return 'YES'

    return 'NO'


