def solution(stdin: str) -> str:
    N = int(stdin.readline())
    segments = [tuple(map(int, stdin.readline().split())) for _ in range(N)]
    segments.sort(key=lambda s: s[1])
    intersections = [0] * (N + 1)
    for i, s1 in enumerate(segments):
        if i == 0:
            intersections[i + 1] = s1[1]
            continue
        intersections[i + 1] = max(intersections[i], s1[0])

    min_intersections = N - 1
    for i in range(N):
        curr = intersections[i]
        for j in range(i + 1, N + 1):
            if intersections[j] < curr + 1:
                break
            curr = intersections[j]
        min_intersections = min(min_intersections, j - i)

    return str(min_intersections)

