            if start > finish:
                return
            if start == finish:
                return True
            if not dp[start][finish]:
                dfs(start + 1, finish, count + 1)
                if s[start]!= t[finish]:
                    dfs(start + 1, finish + 1, count + 1)

        n = int(stdin.readline())
        s = stdin.readline().strip()
        t = stdin.readline().strip()
        dp = [[False] * (n + 1) for _ in range(n + 1)]
        for i in range(n + 1):
            for j in range(n + 1):
                if i == j:
                    dp[i][j] = True
                    continue
                if i > j:
                    break
                if s[i]!= t[j]:
                    dp[i][j] = False
                    continue
                if s[i] == t[j]:
                    dfs(i + 1, j + 1, 0)
                    if dp[i][j]:
                        return dp[i][j]
        return '-1'

    return solve()


