    def helper(s, t):
        memo = {}

        def dfs(l, r):
            if (l, r) in memo:
                return memo[(l, r)]

            if r - l == len(t):
                return l

            res = float('-inf')
            for i in range(l + 1, r):
                if s[i] == t[r - l]:
                    res = max(res, dfs(l + 1, i + 1))
            memo[(l, r)] = res
            return res

        return dfs(0, len(s))

    s, t = stdin.splitlines()
    return str(helper(s, t))

