            if page_cups[0][i] > cups_of_coffee:
                page_cups[0][i] -= cups_of_coffee
            else:
                cups_of_coffee -= page_cups[0][i]
                page_cups[0][i] = 0

        result += 1

        for i in range(len(page_cups) - 1, -1, -1):
            for j in range(len(page_cups[i])):
                if page_cups[i][j] > 0:
                    page_cups[i][j] -= 1
                    break

