def

def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    skill = list(map(int, stdin.split()))

    def valid_teams(n, k, skill):
        skill = sorted(skill)
        c = 1
        for i in range(len(skill)-1):
            if skill[i] + 5 >= skill[i+1]:
                c += 1
        return c <= k

    def rec(n, k, skill, c=1):
        if len(skill) == 2:
            return n
        else:
            if valid_teams(n, k, skill):
                a = [rec(n-1, k, skill, c)]
                for i in range(1, n):
                    a.append(rec(i, k-1, skill, c+1))
                return max(a)

    return str(rec(n, k, skill))

