def solution(stdin: str) -> str:
    # read input
    n = int(stdin.split()[0])
    divisors = list(map(int, stdin.split()[1].split()))

    # find common divisors
    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    common_divisors = set(divisors)
    for d in divisors:
        common_divisors.discard(d)
    common_divisors = list(common_divisors)

    # sort common divisors
    common_divisors.sort()

    # check if list of common divisors is sorted
    if common_divisors!= list(range(1, n + 1)):
        common_divisors.sort()

    # check if common divisors are a permutation of the list of integers
    if sorted(common_divisors) == divisors:
        # find common divisor
        common_divisor = divisors[0]
        for d in divisors:
            if gcd(d, common_divisor) == 1:
                common_divisor = d
                break

        # find prime factors of common divisor
        prime_factors = [d for d in common_divisor if common_divisor % d == 0]

        # find x and y such that x * common_divisor = prime_factors
        x = 1
        for d in prime_factors:
            x *= d

        y = 1
        for d in divisors:
            if d!= common_divisor:
                y *= d

        return str(x) + " " + str(y)
    else:
        return "No solution exists."

