        row = list(map(int, stdin.split()))
        a.append(row)

    def divide_and_conquer(left, right):
        nonlocal a, k
        if left == right:
            return a[left][left] * (right + 1) % k

        mid = (left + right) // 2
        left_sum = divide_and_conquer(left, mid)
        right_sum = divide_and_conquer(mid + 1, right)

        for i in range(n):
            a[i] = [0] * m
            a[i][mid] = 1

        return max(left_sum, right_sum)

    return str(divide_and_conquer(0, m - 1))

