    n, x, y = map(int, stdin.split())
    num = int(stdin[1:])
    MOD = 10**9 + 7

    def binary_search(l, r, target):
        while l <= r:
            mid = (l + r) // 2
            if mid * mid > target:
                r = mid - 1
            elif mid * mid < target:
                l = mid + 1
            else:
                return mid
        return l

    def pow_mod(a, n):
        res = 1
        while n > 0:
            if n & 1:
                res = (res * a) % MOD
            a = (a * a) % MOD
            n >>= 1
        return res

    def count_operations(n, x, y):
        if n == 1:
            return 1 if num % 10 == 0 else 0
        digit = num % 10
        new_digit = pow_mod(digit, x)
        if new_digit == 0:
            return count_operations(n - 1, x, y)
        elif new_digit == 1:
            if y == 0:
                return count_operations(n - 1, x, y)
            if y == 1:
                return count_operations(n - 1, x, y - 1)
            else:
                target = (pow_mod(10, y) - 1) * pow_mod(10, x - 1) % MOD
                l = 1
                while True:
                    r = binary_search(l, n, target)
                    if r == n:
                        l = r + 1
                    else:
                        return l + 1

    return str(count_operations(n, x, y))

