                res = max(res, backtrack(chosen, index + 1) + backtrack(chosen, index + s[1] - s[0] + 1))

        chosen.remove(index)
        return res

    return f"{max(backtrack(set(), 0))}\n{len(segments)}\n{' '.join(map(str, sorted(segments[i][0] for i in range(len(segments))))))}"


