    # Check if a string is palindrome after applying the operation
    def apply_operation(s: str, i: int, j: int) -> str:
        s = list(s)
        s[i], s[j] = s[j], s[i]
        return ''.join(s)

    # Find the minimum number of hugs needed to make S palindrome
    def min_hugs(s: str, n: int) -> int:
        i = 0
        min_hugs = n
        while i < n - 1:
            if is_palindrome(s[i:]) and (not is_palindrome(s[:i])):
                # Check if the palindrome is possible after the first i characters
                min_hugs = min(min_hugs, min_hugs + 1)
            i += 1
        return min_hugs

    # Apply the operations to make S palindromic
    def make_palindrome(s: str) -> str:
        n = len(s)
        min_hugs = min_hugs(s, n)
        for i in range(n):
            for j in range(i + 1, n):
                s = apply_operation(s, i, j)
                if is_palindrome(s):
                    return s
        return s

