    # Build adjacency list
    adj_list = [[] for _ in range(n + 1)]
    for i in range(m):
        a, b = map(int, stdin.split()[1:3])
        adj_list[a].append(b)
        adj_list[b].append(a)

    # Bellman-Ford algorithm to find shortest path distances
    dist = [float('inf')] * (n + 1)
    dist[1] = 0
    for _ in range(m):
        for u in range(1, n + 1):
            for v in adj_list[u]:
                if dist[u]!= float('inf') and dist[u] + 1 < dist[v]:
                    dist[v] = dist[u] + 1

    # Check if the graph is connected
    for u in range(1, n + 1):
        for v in adj_list[u]:
            if dist[u]!= float('inf') and dist[u] + 1 < dist[v]:
                return "IMPOSSIBLE"

    # Construct the set of connected components
    components = []
    for u in range(1, n + 1):
        if u in components or not set(adj_list[u]).intersection(set(components)):
            components.append(u)

    # Count the number of connected components
    num_components = len(components)

    # Find the minimum distance for each component
    min_distances = [float('inf')] * num_components
    min_distances[0] = 0
    for _ in range(m):
        for u in range(1, n + 1):
            for v in adj_list[u]:
                if dist[u]!= float('inf') and dist[u] + 1 < dist[v]:
                    if components[u - 1]!= components[v - 1]:
                        min_distances[components[u - 1] - 1] = min(min_distances[components[u - 1] - 1], dist[v] + 1)

    # Find the maximum distance for each component
    max_distances = [0] * num_components
    max_distances[0] = 0
    for _ in range(m):
        for u in range(1, n + 1):
            for v in adj_list[u]:
                if dist[u]!= float('inf') and dist[u] + 1 < dist[v]:
                    if components[u - 1]!= components[v - 1]:
                        max_distances[components[u - 1] - 1] = max(max_distances[components[u - 1] - 1], dist[v] + 1)

    # Construct all possible choices of roads
    choices = []
    for u in range(1, n + 1):
        for v in adj_list[u]:
            if dist[u]!= float('inf') and dist[u] + 1 < dist[v]:
                for i in range(num_components):
                    for j in range(num_components):
                        if i!= j:
                            if min_distances[i] == 0 or max_distances[j] == 0:
                                continue
                            if min_distances[i] <= max_distances[j]:
                                break
                        else:
                            if min_distances[i] == 0:
                                continue
                            if min_distances[i] < max_distances[j]:
                                break
                    else:
                        for k in range(len(components) - 1):
                            if components[u - 1]!= components[k]:
                                choices.append('1' * k + '0' * (num_components - 1 - k))
                            else:
                                choices.append('0' * k + '1' * (num_components - 1 - k))

    # Return the number of choices and the choices
    return str(len(choices)) + '\n' + '\n'.join(choices)

def solution(stdin: str) -> str:
    n, m, k = map(int, stdin.split())
    edges = [tuple(map(int, stdin.split())) for _ in range(m)]
    graph = {i: [] for i in range(1, n + 1)}
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def bfs(city, visited):
        visited[city] = True
        queue = [city]
        while queue:
            city = queue.pop(0)
            for neighbor in graph[city]:
                if not visited[neighbor]:
                    queue.append(neighbor)
                    visited[neighbor] = True
        return visited

    def min_distance(city, visited):
        visited[city] = True
        min_dist = float('inf')
        for neighbor in graph[city]:
            if not visited[neighbor]:
                dist = min_distance(neighbor, visited)
                if dist!= float('inf'):
                    min_dist = min(min_dist, dist + 1)
        return min_dist

    total_dist = 0
    for city in range(1, n + 1):
        visited = [False] * (n + 1)
        dist = min_distance(city, visited)
        if dist!= float('inf'):
            total_dist += dist

    min_rods = min(total_dist, n - 1)
    res = []

    def dfs(city, visited, used_rods, current_rod, used_rods_sum):
        if used_rods == min_rods:
            current_rod_str = ''.join(map(str, used_rods))
            res.append(current_rod_str)
            return

        if used_rods_sum > total_dist:
            return

        if current_rod == min_rods:
            current_rod = 0

        for i in range(1, n + 1):
            if not used_rods[i - 1] and not visited[i]:
                visited[i] = True
                used_rods[i - 1] = True
                used_rods_sum += 1
                dfs(i, visited, used_rods, current_rod + 1, used_rods_sum)
                used_rods[i - 1] = False
                visited[i] = False
                current_rod -= 1

        if current_rod == 0:
            current_rod = min_rods
            used_rods_sum -= 1
            dfs(city, visited, used_rods, current_rod, used_rods_sum)

    visited = [False] * (n + 1)
    dfs(1, visited, [False] * (n + 1), 0, 0)

    return '\n'.join(res)


