    for _ in range(m):
        a, b = map(int, stdin.split())
        roads[a-1].append(b-1)
        roads[b-1].append(a-1)

    def dfs(start: int, used: list, result: list) -> list:
        if start == n - 1:
            result.append(''.join(str(i+1) if i in used else '0' for i in range(n)))
            return result

        used.append(start)
        result = dfs(start + 1, used, result)

        used.pop()
        result = dfs(start + 1, used, result)
        return result

    def backtrack(used: list, result: list) -> list:
        if len(used) == n - 1:
            result.append(''.join(str(i+1) if i in used else '0' for i in range(n)))
            return result

        for road in roads[used[-1]]:
            if road not in used:
                result = backtrack(used + [road], result)
        return result

    result = dfs(0, [], [])
    result = backtrack([], result)

    return str(len(result)) + '\n' + '\n'.join(result)

