    H, W, K = map(int, stdin.split())
    grid = [list(row) for row in stdin.split('\n')[1:-1]]
    count = 0

    def backtrack(row: int, col: int, used_rows: set, used_cols: set) -> None:
        nonlocal count
        if count == K:
            return

        if row == H:
            return
        if col == W:
            return

        if row not in used_rows and col not in used_cols:
            count += 1
            used_rows.add(row)
            used_cols.add(col)
            backtrack(row + 1, col, used_rows, used_cols)
            used_rows.remove(row)
            used_cols.remove(col)
            count -= 1

    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                backtrack(i, j, set(), set())

    return str(count)

