        if l[a] == '?' or r[b] == '?':
            return True
        return l[a] == l[b] or r[a] == r[b]

    def max_compatible_pairs(n, l, r, start=0, end=1):
        if start == end:
            return 0 if l[start] == r[end] or l[start] == '?' or r[end] == '?' else 1
        max_left, max_right = 0, 0
        for i in range(start, end):
            if l[i] == r[end]:
                continue
            if l[i] == '?' or r[end] == '?':
                max_left, max_right = max(max_left, max_right), max(max_left, max_right)
            else:
                max_left, max_right = max(max_left, max_right), max(max_left + 1, max_right)
        for i in range(end, n):
            if l[start] == r[i]:
                continue
            if l[start] == '?' or r[i] == '?':
                max_left, max_right = max(max_left, max_right), max(max_left, max_right)
            else:
                max_left, max_right = max(max_left, max_right), max(max_left + 1, max_right)
        return max(max_left, max_right)

    max_pairs = 0
    pairs = []
    for i in range(n):
        for j in range(i + 1, n):
            if is_compatible(i, j):
                pairs.append((i, j))
                max_pairs = max(max_pairs, max_compatible_pairs(n, l, r))
