    edges.sort(key=lambda x: (x[0], x[1]))

    def dfs(visited, path, res):
        if len(path) == n:
            res.add(tuple(path))
            return
        for i in range(m):
            if i > 0 and edges[i][0] == edges[i - 1][1]:
                continue
            if (edges[i][0], path[-1]) not in visited and (edges[i][1], path[-1]) not in visited:
                visited.add((edges[i][0], path[-1]))
                visited.add((edges[i][1], path[-1]))
                dfs(visited, path + [edges[i][0]], res)
                visited.remove((edges[i][0], path[-1]))
                visited.remove((edges[i][1], path[-1]))

    res = set()
    visited = set()
    dfs(visited, [1], res)
    return str(len(res))

