        for neighbor in graph[v]:
            if not visited[neighbor]:
                result += dfs(neighbor, visited)
        return result

    def bfs(start: int, visited: List[int]) -> int:
        queue = [start]
        visited[start] = True
        result = 1
        while queue:
            v = queue.pop(0)
            for neighbor in graph[v]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    queue.append(neighbor)
                    result += 1
        return result

