        flavors = []
        for i in range(start, len(apples)):
            flavors.append(apples[i])
            flavors.sort()
            if len(flavors) == n:
                break
        if len(flavors)!= n:
            return float('inf')
        return abs(flavors[0] - sum(flavors))

    best_apple = 1
    best_diff = float('inf')

    for i in range(1, n + 1):
        diff = min_difference(apples, i)
        if diff < best_diff:
            best_diff = diff
            best_apple = i

    return str(best_apple + l)

