def solution(stdin: str) -> str:
    S, T = stdin.split()
    n, m = len(S), len(T)

    if n!= m:
        return "IMPOSSIBLE"

    operations = 0
    for i in range(n):
        if S[i]!= T[i]:
            operations += 1
            if operations > 4:
                return "IMPOSSIBLE"
            if T[i]!= " ":
                T = T[:i] + T[i].lower() + T[i+1:]
                n -= 1
            else:
                T = T[:i] + T[i+1:]
                n -= 1
                m -= 1

    return str(operations)

