        if len(names) < 3:
            return False
        first_char = names.pop()
        for name in names:
            if name[0]!= first_char:
                return False
        return True

    def valid_ways(names: set) -> int:
        if is_valid(names):
            return 1
        if len(names) < 3:
            return 0

        valid = 0
        for name in names:
            new_names = set(names)
            new_names.remove(name)
            valid += valid_ways(new_names)

        return valid

    return str(valid_ways(names))

