    c_t.sort()

    def binary_search(t_limit: int) -> int:
        left, right = 0, len(c_t) - 1
        while left < right:
            mid = (left + right) // 2
            if c_t[mid][1] > t_limit:
                right = mid
            else:
                left = mid + 1
        return left

    smallest_cost = c_t[0][0]
    for t_limit in range(t + 1):
        index = binary_search(t_limit)
        if index < n and c_t[index][1] == t_limit:
            smallest_cost = c_t[index][0]
            break

    return str(smallest_cost) if smallest_cost!= t + 1 else "TLE"

