    P, Q, R = map(int, stdin.split())
    flights = [
        [1, P],
        [2, Q],
        [3, R]
    ]

    def search(flights, start, target, visited):
        if start == target:
            return 0
        min_time = float('inf')
        for i, flight in enumerate(flights):
            if flight[0] not in visited and flight[1] < min_time:
                min_time = flight[1]
        return min_time

    def dfs(flights, start, target, visited):
        visited.add(start)
        flights.append([start, search(flights, start, target, visited)])
        for flight in flights:
            if flight[0] in visited:
                continue
            visited.add(flight[0])
            dfs(flights, flight[0], target, visited)
            visited.remove(flight[0])

    visited = set()
    dfs(flights, 0, 3, visited)

    return str(min(flight[1] for flight in flights))

