    def burn_candle(time: int) -> int:
        return time + 1

    def can_move_left(time: int, x_i: int) -> bool:
        return time + 1 < 0

    def can_move_right(time: int, x_i: int) -> bool:
        return time + 1 >= N

    def can_light_candle(time: int, x_i: int) -> bool:
        return time + 1 == x_i

    def min_time(left: int, right: int, move: callable, burn: callable, light: callable) -> int:
        def is_empty(time: int) -> bool:
            return time == N + 1

        left_time = left
        right_time = right
        time = 0

        while not is_empty(left_time) and not is_empty(right_time):
            if left_time!= N + 1 and can_move_left(left_time, x[left_time]):
                left_time = move(left_time)
            elif right_time!= N + 1 and can_move_right(right_time, x[right_time]):
                right_time = move(right_time)
            elif left_time!= N + 1 and right_time!= N + 1 and can_light_candle(left_time, x[left_time]) and can_light_candle(right_time, x[right_time]):
                time = max(time, burn(light(left_time)), burn(light(right_time)))
                left_time += 1
                right_time += 1
            elif left_time!= N + 1 and right_time!= N + 1 and can_light_candle(left_time, x[left_time]) and not can_light_candle(right_time, x[right_time]):
                time = max(time, burn(light(left_time)))
                left_time += 1
            elif left_time!= N + 1 and right_time!= N + 1 and not can_light_candle(left_time, x[left_time]) and can_light_candle(right_time, x[right_time]):
                time = max(time, burn(light(right_time)))
                right_time += 1
            elif left_time!= N + 1 and not can_light_candle(left_time, x[left_time]):
                time = max(time, burn(left_time))
                left_time += 1
            elif right_time!= N + 1 and not can_light_candle(right_time, x[right_time]):
                time = max(time, burn(right_time))
                right_time += 1
            elif left_time == N + 1 and right_time == N + 1:
                break

        return time

    return str(min_time(0, N - 1, move_left, burn_candle, light_candle))

