        for neighbor in [(1, 0), (0, 1), (-1, 0), (0, -1)]:
            neighbor_x, neighbor_y = neighbor
            new_x, new_y = x + neighbor_x, y + neighbor_y
            if 0 <= new_x <= 1 and 0 <= new_y <= 1:
                queue.append((new_x, new_y, level + 1))

    return str(distance)

