                backtrack(indices, current[:i] + indices[i] + current[i + 1:], ans, char_count + 1)

    ans = set()
    backtrack(p, list(range(1, n + 1)), ans, 0)
    backtrack(q, list(range(1, n + 1)), ans, 0)
