def solution(stdin: str) -> str:
    n = int(stdin.split()[0])
    a = list(map(int, stdin.split()[1:]))

    def gcd(a: int, b: int) -> int:
        while b:
            a, b = b, a % b
        return a

    def lcm(a: int, b: int) -> int:
        return (a * b) // gcd(a, b)

    def min_shots(c: int, d: int) -> int:
        return (a[c - 1] * d) + 1

    min_dur = float('inf')
    min_order = []

    for i in range(1, n + 1):
        for j in range(1, n + 1):
            dur = lcm(a[i - 1], a[j - 1])
            if dur < min_dur:
                min_dur = dur
                min_order = [i, j]

    shots = sum(min_shots(i, j) for i in range(1, n + 1) for j in range(i, n + 1))
    return str(shots) +'' + str(min_order)

