    from collections import deque
    from typing import List

    def left_pop(queue: List[int]) -> int:
        return queue.pop(0)

    def right_pop(queue: List[int]) -> int:
        return queue.pop(-1)

    def min_books(queue: List[int]) -> int:
        return len(queue)

    def handle_query(queue: List[int], query: str) -> int:
        type_id, id = map(int, query.split())
        if type_id == 1:  # L
            queue.insert(0, id)
        elif type_id == 2:  # R
            queue.append(id)
        elif type_id == 3:  #?
            if id in queue:
                return left_pop(queue)
            else:
                return right_pop(queue)
        else:
            raise ValueError("Invalid query type")
        return min_books(queue)

    queries = deque(stdin.strip().splitlines())
    queue = []

    for query in queries:
        queue.append(handle_query(queue, query))

    return "\n".join(map(str, queue))


