    def bfs(start: int) -> int:
        queue = [start]
        visited = {start}
        level = 0
        while queue:
            size = len(queue)
            for _ in range(size):
                current = queue.pop(0)
                for neighbor in graph[current]:
                    if neighbor not in visited:
                        queue.append(neighbor)
                        visited.add(neighbor)
            level += 1
        return level

