        has_path[start] = True
        for neighbor in graph[start]:
            if dfs(neighbor) == 2:
                return 2
        has_path[start] = False
        return 0

    first_level = bfs(1)
    second_level = bfs(n)
    if first_level!= second_level:
        return "IMPOSSIBLE"
    if dfs(1) == 2:
