        while queue:
            i, j = queue.pop(0)

            if i == target_i and j == target_j:
                max_candies = max(max_candies, grid[i - 1][j - 1])
                return max_candies

            if (i, j) not in visited:
                for di, dj in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
