    def lexicographically_min(s: str, k: int) -> str:
        n = len(s)
        if k > n:
            return ''
        if k == 0:
            return s
        min_length = min(k, n)
        for length in range(min_length, 0, -1):
            for i in range(n - length + 1):
                j = i + length
                if s[i:j] == ''.join(sorted(s[i:j])):
                    return s[:i] + sorted(s[i:j]) + s[j:]
        return ''

    def next_permutation(s: str) -> str:
        n = len(s)
        for i in range(n - 2, -1, -1):
            if s[i] < s[i + 1]:
                break
        else:
            return ''
        for j in range(i + 1, n):
            if s[i] < s[j]:
                s[i], s[j] = s[j], s[i]
                return ''.join(s)
        return ''

    def swap(s: str, i: int, j: int) -> str:
        return s[:i] + s[j] + s[i:j] + s[i+1:]

