def solution(stdin: str) -> str:
    N = int(stdin.split()[0])
    A = [int(x) for x in stdin.split()[1:]]
    dp = [[0] * (N + 1) for _ in range(N + 1)]
    for i in range(1, N + 1):
        for j in range(N + 1):
            if i == j:
                dp[i][j] = abs(A[i - 1] - 0)
            else:
                dp[i][j] = float('inf')
                for k in range(N + 1):
                    if k == i:
                        continue
                    dp[i][j] = min(dp[i][j], dp[k][j] + abs(A[i - 1] - A[k]))
    total_cost = 0
    for i in range(1, N + 1):
        if i == 1 or i == N:
            total_cost += abs(A[i - 1] - 0)
        else:
            total_cost += dp[i][i] + dp[i][i + 1] + abs(A[i - 1] - A[i]) + abs(A[i] - A[i + 1])
    return f'{total_cost}\n' + '\n'.join(map(str, dp[1][N]))

