def solution(stdin: str) -> str:
    N, *A = map(int, stdin.split())
    A = [tuple(A[i:i+2]) for i in range(0, len(A), 2)]

    def cost(a: tuple, b: tuple) -> int:
        return abs(a[0] - b[0]) + abs(a[1] - b[1])

    def visit(i: int, visited: set) -> int:
        if i not in visited:
            visited.add(i)
            return cost(A[i-1], A[i])
        else:
            return 0

    visited = {1}
    result = [0] * N
    for i in range(2, N+1):
        result[i-2] = visit(i, visited)

    visited = {1, N}
    result[0] = visit(1, visited)
    result[N-1] = visit(N, visited)

    return '\n'.join(map(str, result))

