    S = stdin.split()[1]

    def backtrack(start: int, used: set) -> int:
        if start == N:
            return 0

        if N - start > 2:
            if start + 2 <= N:
                used.add(S[start:start + 3])
            if start + 1 <= N:
                used.add(S[start:start + 2])
        else:
            used.add(S[start])

        return max(backtrack(start + 1, used), backtrack(start + 2, used))

    return str(backtrack(0, set()))

