    N = int(stdin.split()[0])
    A = list(map(int, stdin.split()[1:]))

    def max_rectangle_area(heights: list, left: int, right: int) -> int:
        area = 0
        i = left
        while i < right:
            area = max(area, heights[i])
            i += 1
        return area

    def max_rectangle_area_stack(heights: list) -> int:
        stack = []
        max_area = 0
        i = 0
        while i < len(heights):
            if not stack or heights[stack[-1]] <= heights[i]:
                stack.append(i)
                i += 1
            else:
                j = stack.pop()
                area = (i - 1) * heights[j]
                max_area = max(max_area, area)
        while stack:
            j = stack.pop()
            area = (i - 1) * heights[j]
            max_area = max(max_area, area)
            i += 1
        return max_area

    heights = A[::-1]
    left = 0
    right = len(heights)
    max_area = max_rectangle_area_stack(heights)

    if max_area == 0:
        return str(max_area)

    while left < right:
        mid = (left + right) // 2
        heights = A[::-1][:mid] + A[::-1][mid:]
        left_area = max_rectangle_area_stack(heights)
        if left_area > max_area:
            max_area = left_area
            left = mid
        else:
            right = mid

    return str(max_area)

