def solution(stdin: str) -> str:
    def count_ways(A: int, B: int, C: int, X: int) -> int:
        if X % 50!= 0:
            raise ValueError("X must be a multiple of 50")
        def is_valid(coins: list[int]) -> bool:
            total = sum(coins)
            if total < X:
                return False
            if total % 50!= 0:
                return False
            num_A, num_B, num_C = 0, 0, 0
            for coin in coins:
                if coin == 500:
                    num_A += 1
                elif coin == 100:
                    num_B += 1
                else:
                    num_C += 1
            return num_A == B and num_B == C and num_C == A
        def backtrack(A: int, B: int, C: int, coins: list[int], memo: list[list[int]]):
            if A == 0 and B == 0 and C == 0:
                return 1
            if (A, B, C) in memo:
                return memo[(A, B, C)]
            if is_valid(coins):
                memo[(A, B, C)] = 1
                memo[(A - 500, B, C)] += backtrack(A - 500, B, C, [500] + coins, memo)
                memo[(A, B - 100, C)] += backtrack(A, B - 100, C, [100] + coins, memo)
                memo[(A, B, C - 50)] += backtrack(A, B, C - 50, [50] + coins, memo)
                memo[(A, B, C)] -= backtrack(A, B, C, [500] + coins, memo)
                memo[(A, B, C)] -= backtrack(A, B, C, [100] + coins, memo)
                memo[(A, B, C)] -= backtrack(A, B, C, [50] + coins, memo)
            else:
                memo[(A, B, C)] = 0
            return memo[(A, B, C)]
        return backtrack(A, B, C, [], {})

    A, B, C, X = map(int, stdin.split())
    return str(count_ways(A, B, C, X))

