def solution(stdin: str) -> str:
    # Reading input
    n = int(stdin.readline())
    a = list(map(int, stdin.readline().split()))

    # Checking if all elements are equal
    if len(set(a)) == 1:
        return "0"

    # Calculating the minimum sum of squares
    total_sum_squares = sum([x**2 for x in a])
    min_sum_squares = total_sum_squares
    for i in range(n):
        for j in range(i+1, n):
            if a[i]!= a[j]:
                new_sum_squares = total_sum_squares - (a[i]**2 + a[j]**2)
                if new_sum_squares < min_sum_squares:
                    min_sum_squares = new_sum_squares

    # Checking if there is a valid value of D
    if min_sum_squares == total_sum_squares:
        return "-1"

    # Calculating the minimum value of D
    min_D = min_sum_squares**0.5
    if min_D.is_integer():
        return str(int(min_D))
    else:
        return "-1"

