    s = stdin.split()[1]

    if k > 2**n:
        return -1

    dp = [0] * (1<<n)
    dp[0] = 0

    for i in range(1, n+1):
        for j in range(1, (1<<n)+1):
            if j & (1<<i-1) == 0:
                dp[j] = min(dp[j], dp[j^(1<<i)] + (n-i))

