def solution(stdin: str) -> str:
    """
    Solution to the problem "Tree with Degree D"
    """
    # Read input
    n, m, D = map(int, stdin.readline().split())
    edges = [tuple(map(int, line.split())) for line in stdin.readlines()]

    # Check if there is a spanning tree with required degree
    for i in range(1, n+1):
        used_edges = []
        used_vertices = [False] * n
        used_vertices[i-1] = True
        for j in range(m):
            if edges[j][0]-1 == i-1 or edges[j][1]-1 == i-1:
                # Check if edge (i, v) is not used and (v, i) is not used
                if edges[j][0]-1 not in used_vertices and edges[j][1]-1 not in used_vertices:
                    used_vertices[edges[j][0]-1] = True
                    used_vertices[edges[j][1]-1] = True
                    used_edges.append(j)
                elif edges[j][0]-1 in used_vertices and edges[j][1]-1 in used_vertices:
                    used_edges.append(j)
                else:
                    break
        if len(used_edges) == n-1 and len(set(used_edges)) == n-1:
            # Construct a spanning tree with required degree
            spanning_tree = []
            for j in used_edges:
                if edges[j][0]-1 == i-1:
                    spanning_tree.append((edges[j][0], edges[j][1]))
                else:
                    spanning_tree.append((edges[j][1], edges[j][0]))
            # Check if the degree of the first vertex is equal to D
            if len([v for v in used_vertices if v]) == D:
                # Print output
                output = "YES\n"
                for edge in spanning_tree:
                    output += f"{edge[0]} {edge[1]}\n"
                return output

    # No spanning tree with required degree found
    return "NO"

