def solution(stdin: str) -> str:
    for t in range(int(stdin.readline().strip())):
        n, m = map(int, stdin.readline().strip().split())
        types = []
        for i in range(n):
            top_left, top_right, bottom_left, bottom_right = map(int, stdin.readline().strip().split())
            types.append([[top_left, top_right], [bottom_left, bottom_right]])

        def find_empty(board):
            for i in range(m):
                for j in range(m):
                    if board[i][j] == -1:
                        return i, j
            return None

        def is_valid(board, i, j, type_idx):
            if i < 0 or i >= m or j < 0 or j >= m:
                return False
            if board[i][j]!= -1:
                return False
            if type_idx!= None and (board[i][j]!= types[type_idx][0][0] and board[i][j]!= types[type_idx][0][1] and board[i][j]!= types[type_idx][1][0] and board[i][j]!= types[type_idx][1][1]):
                return False
            if i > 0 and board[i-1][j] == board[i][j]:
                return False
            if j > 0 and board[i][j-1] == board[i][j]:
                return False
            return True

        board = [[-1]*m for _ in range(m)]
        i, j = 0, 0
        board[i][j] = types[0][0][0]

        type_idx = 0
        while True:
            empty = find_empty(board)
            if empty == None:
                break
            i, j = empty
            if type_idx == len(types)-1:
                type_idx = 0
            else:
                type_idx += 1
            for type_j in range(2):
                for type_i in range(2):
                    if is_valid(board, i+type_i-1, j+type_j-1, type_idx):
                        board[i+type_i-1][j+type_j-1] = types[type_idx][type_i][type_j]
                        break
                else:
                    return "NO"

        for i in range(m):
            if board[i][i]!= board[i][m-1-i]:
                return "NO"
        return "YES"

