def solution(stdin: str) -> str:
    n = int(stdin.split('\n')[0])
    s = stdin.split('\n')[1]

    # check if string is already sorted
    if s == ''.join(sorted(s)):
        return "YES\n" + ''.join(['0' if i%2==0 else '1' for i in range(n)])

    # check if string can be colored so that it can be sorted
    for i in range(n):
        if s[i]!= s[i-1] and s[i]!= s[i+1]:
            # swap with previous character if it is colored the same color
            if s[i] == s[i-1]:
                s = s[:i-1] + s[i] + s[i-1] + s[i+1:]
            # swap with next character if it is colored the same color
            elif s[i] == s[i+1]:
                s = s[:i] + s[i+1] + s[i] + s[i+2:]
            # color character with color of previous character
            else:
                s = s[:i] + '1' + s[i+1:]
            return "YES\n" + ''.join(['0' if i%2==0 else '1' for i in range(n)])

    return "NO\n"

