def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    if n > k:
        return "NO"
    a = [1] * k
    total_problems = k
    for i in range(k):
        if total_problems < n:
            return "NO"
        if total_problems - a[i] > 2 * a[i]:
            a[i] += 1
            total_problems -= 1
        else:
            total_problems -= a[i]
    return "YES\n" + " ".join(map(str, a))

