def solution(stdin: str) -> str:
    n, m, d = map(int, stdin.split()[0:3])
    c = list(map(int, stdin.split()[3:]))
    platforms = [(0, c[0])]
    for i in range(1, m):
        platforms.append((platforms[-1][1], platforms[-1][1] + c[i]))
    jumps = [0] * (n + 2)
    for platform in platforms:
        start, end = platform
        jumps[start] = platform[0] + 1
        jumps[end] = platform[0] + d
    for i in range(n, 0, -1):
        if jumps[i] == 0:
            jumps[i] = jumps[i + 1]
    for i in range(1, n + 2):
        if jumps[i] == 0:
            jumps[i] = jumps[i - 1]
    if jumps[n + 1] == 0:
        return "NO"
    else:
        a = [0] * n
        for i in range(1, m + 1):
            start, end = platforms[i - 1]
