def solution(stdin: str) -> str:
    n, d, k = map(int, stdin.split())
    if d % 2 == 0 or k < d - 1 or (n - 1) * 2 < k:
        return "NO"
    # Prim's algorithm
    import heapq
    heap = [(0, 1)]
    visited = set()
    parent = [0] * (n + 1)
    while heap:
        cost, u = heapq.heappop(heap)
        if u in visited:
            continue
        visited.add(u)
        if len(visited) == n:
            break
        for v in range(1, n + 1):
            if v not in visited and cost + 1 <= d and len(visited) + (n - 1 - len(visited)) // len(set(parent[:len(visited)])) <= k:
                heapq.heappush(heap, (cost + 1, v))
                parent[v] = u
    if len(visited)!= n:
        return "NO"
    # Print tree
    result = "YES\n"
    for u in range(1, n):
        result += f"{parent[u]} {u}\n"
    return result

