        # Remove the tallest stack
        stacks[max_index].pop()
        # Slide down the stacks
        for i in range(max_index+1, n):
            if len(stacks[i]) > 0 and len(stacks[i-1]) > 0:
                if len(stacks[i]) == len(stacks[i-1]):
