    def max_abs_diff(n: int, m: int) -> int:
        """
        Returns the maximum possible value of sum of absolute differences between adjacent (consecutive) elements for an array of length n and sum m.
        """
        if n == 1:
            return max(0, m - 1)
        else:
            max_val = float('-inf')
            for i in range(1, m+1):
                max_val = max(max_val, max_abs_diff(n-1, m-i))
