    n, m = map(int, stdin.split()[0:2])
    a = list(map(int, stdin.split()[2:]))

    # Sort the cups by caffeine dosage in descending order
    sorted_a = sorted(enumerate(a), key=lambda x: x[1], reverse=True)

    # Count the number of cups that can be drunk
    k = 0
    for i in range(n):
        if sorted_a[i][1] > 0:
            k += 1
        else:
            break

    # If all cups are empty, we cannot write the coursework
    if k == 0:
        return '-1'

    # Compute the number of pages that can be written in each day
    # We start with k pages that can be written in the first day
    max_pages = [k]

    # For each cup after the first, we can write at most max(0, dosage - i) pages
    for i in range(1, k):
        max_pages.append(max(0, sorted_a[i][1] - i))

    # Compute the maximum number of days we can work
    days = 1
    current_pages = k
    for i in range(k, n):
        # If we cannot write any page in the current day, we need to start a new day
        if current_pages == 0:
            days += 1
            current_pages = max_pages[i - k]
        # Otherwise, we can add at most dosage - i pages to the current day
        else:
            current_pages = min(current_pages + max(0, sorted_a[i][1] - i), max_pages[i - k])

    # If we can write at least one page in the last day, we return the number of days
    if current_pages > 0:
        return str(days)
    # Otherwise, we return -1
    else:
        return '-1'

